// src/App.js
import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './components/HomePage';
import AboutMe from './components/AboutMe';
import Education from './components/Education';
import Portfolio from './components/Portfolio';
import ContactMe from './components/ContactMe';
import './app.css'; // Your main CSS file

function App() {
  const [activeSection, setActiveSection] = useState('home');

  const renderSection = () => {
    switch (activeSection) {
      case 'about':
        return <AboutMe />;
      case 'education':
        return <Education />;
      case 'portfolio':
        return <Portfolio />;
      case 'contact':
        return <ContactMe />;
      default:
        return <Home />;
    }
  };

  return (
    <div className="container">
      <Header />
      <div id="menu" className="fas fa-bars"></div>


      <nav>
        <button onClick={() => setActiveSection('home')}>Home</button>
        <button onClick={() => setActiveSection('about')}>About Me</button>
        <button onClick={() => setActiveSection('education')}>Education</button>
        <button onClick={() => setActiveSection('portfolio')}>Portfolio</button>
        <button onClick={() => setActiveSection('contact')}>Contact Me</button>
      </nav>

      {/* Render the active section */}
      {renderSection()}

      <Footer />
    </div>
  );
}

export default App;
